import React, { useEffect, useState } from 'react';
import Modal from '../modal';
import {
 saveNewsLetter
} from '../../apis/newsletter';

const NewsLetter = () => {
    const [openNewsLetter, setOpenNewsLetter] = useState(false);
    const [isSaving, setIsSaving] = useState(false);

    const formState = {
        email: '',
        firstName: '',
        lastName: ''
    };
    const [user, setUser] = useState(formState);
    const [error, setError] = useState('');

    useEffect(() => {
        setOpenNewsLetter(true);
    }, [])

    const submit = async (e) => {
        e.preventDefault();

        if (!user.email || !user.firstName || !user.lastName) {
            setError('field is mandatory');
            return;
        }

        setIsSaving(true);
        const newsLetter = await saveNewsLetter(user);
        console.log(newsLetter)
    }

    const renderModalBody = () => {
        return (
            <React.Fragment>
                {error ? 
                    <div className="alert alert-danger" role="alert">
                        {error}
                    </div> : null
                }
                <form>
                    <div className="mb-3">
                        <label className="col-form-label">Email:</label>
                        <input type="text" className="form-control"
                        value={user['email']}
                        onChange={(e) => setUser({
                            ...user, email: e.target.value
                        })}
                        disabled={isSaving}
                        />
                    </div>
                    <div className="row row-cols-1 mb-3 text-center">
                        <div className="col-6">
                            <div className="mb-3">
                                <label className="col-form-label">First Name:</label>
                                <input type="text" className="form-control"
                                value={user['firstName']}
                                onChange={(e) => setUser({
                                    ...user, firstName: e.target.value
                                })}
                                disabled={isSaving}
                                />
                            </div>
                        </div>
                        <div className="col-6">
                            <div className="mb-3">
                                <label className="col-form-label">Last Name:</label>
                                <input type="text" className="form-control"
                                value={user['lastName']}
                                onChange={(e) => setUser({
                                    ...user, lastName: e.target.value
                                })}
                                disabled={isSaving}
                                />
                            </div>
                        </div>
                    </div>
                </form>
            </React.Fragment>
        );
    }

    const renderModalFooter = () => {
        return (
            <React.Fragment>
                <button type="button" className="btn btn-secondary"
                data-bs-dismiss="modal" onClick={e => setOpenNewsLetter(false)}>Close</button>
                <button type="button" className="btn btn-primary" onClick={e => submit(e)}>Send message</button>
            </React.Fragment>
        );
    }

    return (
        <React.Fragment>
            <Modal 
            isOpen={openNewsLetter}
            setIsOpen={setOpenNewsLetter}
            renderBody={renderModalBody}
            renderFooter={renderModalFooter}
            />
        </React.Fragment>
    );
}

export default NewsLetter;
