import React, { useState } from 'react';

const Search = () => {
    const [searchBy, setSearchBy] = useState('');

    const search = (e, attr) => {
        e.preventDefault();
        setSearchBy(attr);
    }

    return (
        <React.Fragment>
            <main>
                <div className="row mb-3 text-center">
                    <div className="col">
                    <div className="input-group mb-3">
                        <input type="text" className="form-control" aria-label="Text input with dropdown button" />
                        <button className="btn btn-outline-secondary dropdown-toggle" 
                        type="button" data-bs-toggle="dropdown" aria-expanded="false">{searchBy ?? 'Search By: '}</button>
                        <ul className="dropdown-menu dropdown-menu-end">
                            <li><a className="dropdown-item" href="# " onClick={(e) => search(e, 'name')}>Name</a></li>
                            <li><a className="dropdown-item" href="# " onClick={(e) => search(e, 'country')}>Country</a></li>
                        </ul>
                        </div>
                    </div>
                </div>
            </main>
        </React.Fragment>
    );
}

export default Search;
